/**
 * Statistiken über die Hardware-Daten
 */
export function getHardwareStats() {
  const items = EINSAMOBILE_HARDWARE_2026;
  const brandCounts: Record<string, number> = {};
  const categoryCounts: Record<string, number> = {};
  
  for (const item of items) {
    brandCounts[item.brand] = (brandCounts[item.brand] || 0) + 1;
    categoryCounts[item.category || "smartphone"] = (categoryCounts[item.category || "smartphone"] || 0) + 1;
  }
  
  const prices = items.map(i => i.ek_net);
  
  return {
    total: items.length,
    byBrand: Object.entries(brandCounts).sort((a, b) => b[1] - a[1]),
    byCategory: Object.entries(categoryCounts).sort((a, b) => b[1] - a[1]),
    priceRange: {
      min: Math.min(...prices),
      max: Math.max(...prices),
      avg: prices.reduce((a, b) => a + b, 0) / prices.length,
    },
    validFrom: "2026-01-07",
    source: "einsAmobile Preisliste KW02/2026",
  };
}
